import * as React from 'react';
import { ReactMarkdown } from "react-markdown/lib/react-markdown";
import Navbar from './navbar';
import {Link} from 'react-router-dom'
import SideBar from './sidebar';

const SYSTEM_MESSAGE = "You are ChatVision, an Artificial Intelligence ChatBot created by Priyanshu Sobti. You are one of the most intelligent chatbots out there and you are created using state of the art Machine Learning Models and APIs. You are helpful and savage with your answers at the same time. Give savage replies whenever you want. Generate a proper introduction for yourself everytime you are asked for.";


export default function ChatBot() {
  const [userMessage,setUserMessage] = React.useState("");
  const [messages,setMessages] = React.useState([]);
  const email = localStorage.getItem("user_email");

  React.useEffect(() => {
    const data = localStorage.getItem(email + "_data")
    if(data !== null){
      console.log(messages);
      setMessages(JSON.parse(data))
      console.log(messages);
    }
    else{
      setMessages([{role:"system",content:SYSTEM_MESSAGE}])
    }
  },[
  ])

  const { Configuration, OpenAIApi } = require("openai");
  const configuration = new Configuration({
    apiKey: "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",
  });

  const handleKeyDown = (e) => {
    if(e.key === "Enter" && !e.shiftKey){
      e.preventDefault();
      sendReq();
    }
  };


  const sendReq = async () =>{
    const updatedMessages = [
      ...messages,
      {
        role: "user",
        content: userMessage,
      },
    ];
    setMessages(updatedMessages);
    setUserMessage("");
    const openai = new OpenAIApi(configuration);
    try{
        const completion = await openai.createChatCompletion({
          model: "gpt-3.5-turbo",
          messages: updatedMessages,
        });
        console.log(completion.data.choices[0].message);
        const botMessage = completion.data.choices[0].message;
        const updatedMessages2 = [
          ...updatedMessages,
          botMessage
        ];
        setMessages(updatedMessages2);
        localStorage.setItem(email + "_data",JSON.stringify(updatedMessages2));
    }
    catch(error){
      console.log(error);
    }

  }

  return (
    <>
    <div className="flex flex-col h-screen bg-gray-800 ">
      <Navbar/>
      <SideBar/>
      <div className="flex flex-col h-screen">
        <div className="flex-1 overflow-y-scroll">
        <div className="w-full relative mx-auto pl-40 pr-6 py-4">
          {messages
          .filter((message) =>message.role !== "system")
          .map((message,idx) => (
            <div key = {idx} className="my-3">
              
                {message.role !== "user" && <div>
                  {/* <div className='font-bold text-gray-100'>You</div>
                  <div className="text-lg text-gray-300 prose">
                  <ReactMarkdown>{message.content}</ReactMarkdown>
                </div> */}

                <div class="chat-message">
         <div class="flex items-end">
            <div class="flex flex-col space-y-2 text-xs max-w-xs mx-2 order-2 items-start">
               <div><span class="px-4 py-2 rounded-lg inline-block rounded-bl-none bg-gray-300 text-gray-600"><ReactMarkdown>{message.content}</ReactMarkdown></span></div>
            </div>
            <img src="https://images.unsplash.com/photo-1549078642-b2ba4bda0cdb?ixlib=rb-1.2.1&amp;ixid=eyJhcHBfaWQiOjEyMDd9&amp;auto=format&amp;fit=facearea&amp;facepad=3&amp;w=144&amp;h=144" alt="My profile" class="w-6 h-6 rounded-full order-1"/>
         </div>
      </div>
              
            
            </div>
              }
              {message.role === "user" && <div >
{/* 

                  <div className='font-bold text-gray-100'>ChatVision</div>
                  <div className="text-lg text-gray-300 prose">
                  <ReactMarkdown>{message.content}</ReactMarkdown>
                </div> */}
<div class="chat-message">
         <div class="flex items-end justify-end">
            <div class="flex flex-col space-y-2 text-xs max-w-xs mx-2 order-1 items-end">
               <div><span class="px-4 py-2 rounded-lg inline-block rounded-br-none bg-blue-600 text-white "><ReactMarkdown>{message.content}</ReactMarkdown></span></div>
            </div>
            <img src="https://images.unsplash.com/photo-1590031905470-a1a1feacbb0b?ixlib=rb-1.2.1&amp;ixid=eyJhcHBfaWQiOjEyMDd9&amp;auto=format&amp;fit=facearea&amp;facepad=3&amp;w=144&amp;h=144" alt="My profile" class="w-6 h-6 rounded-full order-2"/>
         </div>
      </div>


                </div>
              }
                </div>
            ))}
          </div>
        </div>
      </div>
      
        <div className=" mx-auto w-full max-w-screen-md mx-200 px-10 pt-0 pb-5 flex sticky bottom-0 ">
          <textarea value={userMessage} onChange={(e) => setUserMessage(e.target.value)} onKeyDown={handleKeyDown} className="border rounded-md text-lg p-2 flex-1" rows={1}placeholder="Type your query" />
          <button onClick={sendReq} className="border rounded-md bg-blue-600 hover:bg-blue-700 text-white px-4 ml-2"><svg className='w-6' xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor" class="h-6 w-6 ml-2 transform rotate-90">
                  <path d="M10.894 2.553a1 1 0 00-1.788 0l-7 14a1 1 0 001.169 1.409l5-1.429A1 1 0 009 15.571V11a1 1 0 112 0v4.571a1 1 0 00.725.962l5 1.428a1 1 0 001.17-1.408l-7-14z"></path>
               </svg></button>
          <button onClick={()=>{
            localStorage.removeItem(email + "_data");
            setMessages([{role:"system",content:SYSTEM_MESSAGE}]);
          }} className="border rounded-md bg-blue-600 hover:bg-blue-700 text-white px-4 ml-2">
          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor" className='h-6'>
            <path fill-rule="evenodd" d="M9 2a1 1 0 00-.894.553L7.382 4H4a1 1 0 000 2v10a2 2 0 002 2h8a2 2 0 002-2V6a1 1 0 100-2h-3.382l-.724-1.447A1 1 0 0011 2H9zM7 8a1 1 0 012 0v6a1 1 0 11-2 0V8zm5-1a1 1 0 00-1 1v6a1 1 0 102 0V8a1 1 0 00-1-1z" clip-rule="evenodd"/>
          </svg></button>

        </div>

        {/* <div className="grid grid-cols-3 p-4">


          <div className="h-200">
            <Link to="/quiz_master" className="block max-w-md p-6 bg-gray-100 border border-gray-200 rounded-lg shadow hover:bg-gray-100">
              <img src="https://i.imgur.com/SYL3f0M.png" className="h-10 w-10"/>
              <text className="font-bold text-xl underline break-after">{"Quiz Master \n"}</text>
              <text>Practice your knowledge and understanding of any topic by dynamic AI-generated multiple choice questions with detailed explanations.</text>
              </Link>
          </div>


          <div className="h-200">
          <Link to="/mock_interview" className="block max-w-md p-6 bg-gray-100 border border-gray-200 rounded-lg shadow hover:bg-gray-100">
              <img src="https://i.imgur.com/CRBxvAz.png" className="h-10 w-10"/>
              <text className="font-bold text-xl underline break-after">{"Mock Interview \n"}</text>
              <text>Simulate an interview for any job position, providing personalized feedback and helping you prepare for your real interview.</text>
          </Link>
          </div>


          <div className="h-200">
          <Link to="/code_explainer" className="block  max-w-md p-6 bg-gray-100 border border-gray-200 rounded-lg shadow hover:bg-gray-100">
              <img src="https://i.imgur.com/gznl8L8.png" className="h-10 w-10"/>
              <text className="font-bold text-xl underline break-after">{"Code Explainer\n"}</text>
              <text>Breaks down any piece of code and provides a clear explanation of the code helping you understand programming concepts and improve your coding skills.</text>
          </Link>
          </div>

        </div> */}
        
    </div>
    </>
  );
}



