import { useState } from "react";

export default function Stable(){

    const [urls,setUrl] = useState([]);
    const [prompt,setPrompt] = useState("");

    const { Configuration, OpenAIApi } = require("openai");
    const configuration = new Configuration({
        apiKey: "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",
    });
    const sendPrompt = async () =>{

        const openai = new OpenAIApi(configuration);
        const response = await openai.createImage({
          prompt: prompt,
          n: 2,
          size: "1024x1024",
        });
        const image_urls = [
            {
                idx:0,
                url:response['data']['data'][0]['url']
            },
            {
                idx:1,
                url:response['data']['data'][1]['url']
            }
        ]
        setUrl(image_urls);
    }

    return (
        <>
        <div className="grid grid-cols-2 w-full max-w-screen-md flex-auto">
                {urls.map((source,idx) => (
                <div key={idx} className="my-3">
                    <img src={source.url} alt="Nothin"/>
                </div>
                ))}
        </div>
        <div className="mx-auto w-full max-w-screen-md px-4 pt-0 pb-2 flex">
          <textarea value={prompt} onChange={(e) => setPrompt(e.target.value)} className="border rounded-md text-lg p-2 flex-1" rows={1}placeholder="Type your query" />
          <button onClick={sendPrompt} className="border rounded-md bg-blue-500 hover:bg-blue-600 text-white px-4 ml-2">Click</button>
        </div>
        </>
    );
}
